import React, { useRef, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function Home() {
  const sliderRef = useRef(null);

  useEffect(() => {
    // Kiểm tra xem sliderRef đã được thiết lập chưa
    if (sliderRef.current) {
      // Gọi các phương thức của sliderRef.current chỉ khi nó đã được thiết lập
      sliderRef.current.slickNext();
      sliderRef.current.slickPrev();
    }
  }, []);

  const goToNext = () => {
    sliderRef.current.slickNext();
  };

  const goToPrev = () => {
    sliderRef.current.slickPrev();
  };

  return (
    <div>
      {/* Các phần khác của trang */}
      <div className="content-section_home">
        <Slider
          ref={sliderRef} // Gán ref cho Slider component
          className="multiple-items"
          infinite={true}
          slidesToShow={3}
          slidesToScroll={3}
        >
          {/* Các mục trong carousel */}
          <div>
            <img src="assets/images/arts/art1.jpeg" alt="Image 1" />
          </div>
          <div>
            <img src="assets/images/arts/art2.jpeg" alt="Image 2" />
          </div>
          <div>
            <img src="assets/images/arts/art3.jpeg" alt="Image 3" />
          </div>
          {/* Thêm các mục khác vào đây */}
        </Slider>
      </div>

      {/* Nút điều hướng */}
      <div className="carousel-controls">
        <button onClick={goToPrev}>Previous</button>
        <button onClick={goToNext}>Next</button>
      </div>
    </div>
  );
}

export default Home;
